INSERT INTO games (id, title, description, price, image) VALUES
                                                            (0, 'Assassin''s Creed Shadows  Édition Standard', 'Disponible sur PlayStation®5, Xbox Series X|S, Ubisoft+, Amazon Luna, les Macs dotés d''Apple Silicon depuis le Mac App Store, ainsi que sur PC Windows depuis l''Ubisoft Store, Steam et l''Epic Games Store. Sortie également prévue sur iPad à l’avenir. Plongez dans une épopée d''action-aventure au cœur du Japon féodal ! Incarnez une Assassin shinobi meurtrière et un puissant samouraï légendaire tout en explorant un magnifique monde ouvert plongé dans le chaos.', 69.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw10cd2e73/images/large/660e5a03fbff4e2940488bcd.jpg?sw=300&sh=395&sm=fit'),
                                                            (1, 'Assassin''s Creed Mirage  Édition Standard', 'Découvrez l''histoire de Basim, voleur à la tire habile mais atteint de visions cauchemardesques, qui cherche désespérément des réponses, mais aussi la justice, dans les rues animées du Bagdad du IXe siècle. Par l''intermédiaire d''une société aussi ancienne que mystérieuse appelée Ceux qu''on ne voit pas, il va devenir un tueur redoutable et son sort va prendre une tournure qu''il n''aurait jamais imaginée.', 49.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dwb1a1bd7b/images/large/62ea4f8e09372571f2736a71.jpg?sw=300&sh=395&sm=fit'),
                                                            (2, 'Assassin''s Creed Valhalla  Édition Standard', 'Devenez une légende Viking en quête de gloire. Partez en raids, améliorez votre campement, et méritez votre place au Valhalla.', 59.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw3edc2aad/images/large/5e849c6c5cdf9a21c0b4e731.jpg?sw=300&sh=395&sm=fit'),
                                                            (3, 'Assassin''s Creed Odyssey  Édition Standard', 'Forgez votre destin dans Assassin''s Creed® Odyssey. Passez du statut de paria à celui de légende vivante au cours d''une véritable odyssée durant laquelle vous lèverez les secrets sur votre passé et changerez le destin de la Grèce antique.', 59.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw77a3416f/images/large/5afda5f788a7e34d25b5012f.jpg?sw=300&sh=395&sm=fit'),
                                                            (4, 'Assassin''s Creed Origins  Édition Standard', 'L''Égypte ancienne, époque de grandeur et d''intrigues, sombre dans une impitoyable lutte pour le pouvoir.', 59.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw7fe341f3/images/large/592450934e0165f46c8b4568.jpg?sw=300&sh=395&sm=fit'),
                                                            (5, 'Assassin''s Creed Syndicate  Édition Standard', 'Londres, 1868. En pleine révolution industrielle, vous devez guider votre organisation clandestine et développer votre influence pour combattre ceux qui exploitent les démunis au nom du progrès. Redécouvrez ce grand classique, désormais à 60 IM/S sur Xbox Series X|S et PS5® !', 39.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw7c02c24b/images/large/56c4947988a7e300458b45c6.jpg?sw=300&sh=395&sm=fit'),
                                                            (6, 'Assassin''s Creed Chronicles  Trilogie', 'Prolongez l''expérience Assassin''s Creed en découvrant les histoires oubliées de trois Assassins dans de nouvelles périodes historiques et avec un gameplay furtif inédit en 2.5D', 24.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw90421959/images/large/57062ec488a7e316728b46a2.jpg?sw=300&sh=395&sm=fit'),
                                                            (7, 'Assassin''s Creed Unity  Édition Standard', 'Dans Assassin''s Creed Unity, vous incarnerez Arno, un jeune homme qui va être plongé dans les rouages de la Révolution Française. Avec le tout nouveau mode Coop, vous découvrirez comment le destin de toute une nation a basculé. Faites preuve de communication et de coordination pour devenir le Maitre Assassin ultime.', 29.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dwcc504b80/images/large/56c4947a88a7e300458b45d6.jpg?sw=300&sh=395&sm=fit'),
                                                            (8, 'Assassin''s Creed Rogue  Édition Standard', 'Amérique du Nord. 18ème siècle. Alors que la Guerre de la Conquête fait rage, Shay Patrick Cormac, un jeune membre de la Confrérie des Assassins va sombrer dans les ténèbres et changer le destin des colonies Américaines.Après une mission ayant tourné au drame, Shay renie les Assassins qui tenteront à leurs tours de le tuer. Trahi par ses frères d’armes, Shay se lance dans une quête de vengeance et décide de traquer ceux qui se sont retournés contre lui. Il deviendra le chasseur d’Assassins le plus redouté de l’Histoire.', 19.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dwa3855356/images/large/56c4947988a7e300458b45bc.jpg?sw=300&sh=395&sm=fit'),
                                                            (9, 'Assassin''s Creed IV Black Flag  Gold Edition', '1715, les Pirates règnent sur les Caraïbes et viennent de fonder leur propre République où la corruption, l’avarice et la cruauté sont désormais monnaie courante.', 39.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw7f2b5ed1/images/large/56c4948088a7e300458b46b0.jpg?sw=300&sh=395&sm=fit'),
                                                            (10, 'Assassin''s Creed Revelations  Édition Standard', 'Quand un homme a remporté toutes ses batailles et a exécuté tous ses ennemis, que lui reste-t-il à accomplir ? Ezio Auditore doit tirer un trait sur sa vie passée pour trouver des réponses, pour mener à bien sa quête de vérité.', 14.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw88ca1535/images/large/56c4948188a7e300458b46d2.jpg?sw=300&sh=395&sm=fit'),
                                                            (11, 'Assassin''s Creed Brotherhood  Édition Standard', 'Poursuivez les aventures d''Ezio dans sa lutte contre l''Ordre des Templiers dont l''influence et la puissance ne cessent de croitre. Devenu un Maitre Assassin légendaire, Ezio continue son périple à travers Rome, centre du pouvoir, de la cupidité et de la corruption, pour frapper l''ennemi en plein coeur. Les fonctionnalités multijoueur et en ligne ne sont plus disponibles pour ce produit.', 14.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw634ad17f/images/large/56c4948088a7e300458b46b6.jpg?sw=300&sh=395&sm=fit'),
                                                            (12, 'Assassin''s Creed III Remastered  Standard Edition', 'Revivez la Révolution Américaine ou vivez-la pour la première fois dans Assassin''s Creed® III Remastered, avec des graphismes améliorés et de nouvelles mécaniques de jeu. Le jeu inclut Assassin''s Creed Liberation Remastered et tous les DLC solo. Les fonctionnalités multijoueur et en ligne ne sont plus disponibles pour ce produit.', 39.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw017eb737/images/large/5c62f61dadc72438053897eb.jpg?sw=300&sh=395&sm=fit'),
                                                            (13, 'Assassin''s Creed II  Édition Standard', 'La suite plébiscitée de Assassin’s Creed présente un nouveau héros, Ezio Auditore da Firenze, un jeune noble italien ainsi qu''une nouvelle ère : la Renaissance. Assassin’s Creed 2 est une histoire épique mêlant famille, vengeance et conspiration sur fond de Renaissance italienne. Ezio qui se lie d’amitié avec Léonard de Vinci, affronte l’une des familles les plus puissantes de Florence et s’aventure à travers les canaux de Venise où il apprend à devenir un maître assassin. Les fonctionnalités multijoueur et en ligne ne sont plus disponibles pour ce produit.', 9.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw5df235dd/images/large/56c4947f88a7e300458b4690.jpg?sw=300&sh=395&sm=fit'),
                                                            (14, 'Assassin''s Creed  Édition Standard', '1191 après Jésus Christ, la Troisième Croisade déchire la Terre Sainte.  Vous êtes, Altaïr, le bretteur le plus talentueux de votre ordre, et devrez tout tenter pour mettre fin aux hostilités en supprimant les chefs des deux camps. Vous êtes un Assassin, guerrier enveloppé de mystère et respecté pour sa cruauté, dont le seul nom suscite l’effroi. Vos actions sèment le chaos autour de vous et seule votre présence lors d’évènements cruciaux changera le cours de l’Histoire de l’Humanité.', 9.99, 'https://store.ubisoft.com/dw/image/v2/ABBS_PRD/on/demandware.static/-/Sites-masterCatalog/default/dw3f3fd3a1/images/large/56c4947f88a7e300458b468e.png?sw=300&sh=395&sm=fit');
